import React, { useState,useEffect } from "react";
import GoogleMapsLoader from "google-maps";
import apiURL from "../config/url";
import Locate from "./components/Location";
import axios  from "axios";
import {googleKey,newsKey,timeKey,weatherKey} from './misc/keys';
export default function App() {
   const [time,setTime] = useState('')
   const [weather,setWeather] = useState('')
   const [news,setNews] = useState([])
   const [area,setArea] = useState('')

   useEffect(()=>{
     const loadmap = async () => {
      GoogleMapsLoader.KEY = googleKey
      GoogleMapsLoader.VERSION = "3.39"
      await GoogleMapsLoader.load(google => {

      let options = {
        center: { lat: 40, lng: -100 },
        scrollwheel: false,
        zoom: 5
      }
      new google.maps.Map(document.getElementById("map"), options)
     })
     }
     loadmap();
   },[])

  locate = async () => {
    let area = $("#PSearch").val();
    // Global Variables
    let map = this.state.map;
    let thetime;
    let ftime;
    //clear news
    this.setState({ news: [] });

    //* Google Api

    let res = await axios.get(apiURL.google, {
      params: {
        address: area,
        key: googleKey
      }
    });
    let data = res.data;
    let name;
    name = data.results[0].address_components[0].long_name;
    const arealat = data.results[0].geometry.location.lat;
    const arealing = data.results[0].geometry.location.lng;

    let zoom;
    data.results[0].types[0] === "locality" ? (zoom = 10) : (zoom = 5);

    GoogleMapsLoader.load(function(google) {
      map.setCenter(new google.maps.LatLng(arealat, arealing));
      map.setZoom(zoom);
    });
  };
  const getNews = async () => {
    try{
//* News Api
    res = await axios.get(apiURL.news, {
      params: {
        q: name,
        apiKey: newsKey,
        pageSize: 6
      }
    });
    let articles = res.data.articles;
    if (articles.length < 0) {
      alert("No News Found");
    } else {
      articles.map(article => {
        const date = article.publishedAt.split("T");
        return this.setState(prevState => ({
          news: [
            ...prevState.news,
            {
              title: article.title,
              url: article.url,
              snippet: article.description,
              img: article.urlToImage,
              source: article.source.name,
              published: date[0]
            }
          ]
        }));
      });
    }
    }catch(er){
      console.log(er)
    }
  }
  const getTime = async () => {
    try{
    //* Time Api
    res = await axios.get(apiURL.time, {
      params: {
        format: "json",
        by: "position",
        lat: arealat,
        lng: arealing,
        key: timeKey
      }
    });
    data = res.data;
    thetime = data.formatted.split(" ");
    ftime = thetime[1];
    this.setState({ time: ftime });
    }catch(er){
      console.log(er)
    }
  }
  const getWeather = async () => {
    try{
    //* Weather Api
    res = await axios.get(apiURL.weather, {
      params: {
        q: name,
        units: "imperial",
        appid: weatherKey
      }
    });
    data = res.data;
    name = data.name;

    let weather = data.weather[0].main;
    let temp = Math.ceil(data.main.temp);
    let wind = data.wind.speed;
    this.setState(() => ({ weather: { name, weather, temp, wind } }));
    }catch(er){
      console.log(er)
    }
  }
  return (
      <Locate click={this.locate} time={time} weather={weather} news={news} />
  )
}
