import React,{useState} from "react";
import {
  Container,
  Typography as Font,
  Button,
  Input,
  Card,
  Grid,
  Paper
} from "@material-ui/core";
import ArticleList from './ArticleList'
const Location = ({locate,area:[area,setArea],time,weather,news}) => {
  /*const weather = () => (
    <div id="weather">
      <Font variant="h2">{props.weather.name}</Font>
      <Font variant="caption">{props.weather.weather}</Font>
      <Font variant="body1">{props.weather.temp}&#176;</Font>
    </div>
  );
*/
  const handleSubmit = e => {
    e.preventDefault();
    locate()
    setArea('')
  };
  const handleChange = ({target:{value}}) => {
      setArea(value)
  }
  return (
    <Container component="article" className="LocationRoot">
      <Font variant="h1" align="center">
        Location Lookup
      </Font>
      <form onSubmit={e => handleSubmit(e)} style={{ textAlign: "center" }}>
        <Input
          placeholder="Enter A Place"
          id="Search"
          value={area}
          onChange={handleChange}
        />
        <Button
          id="SearchButton"
          type="submit"
          variant="outlined"
          color="primary"
        >
          <Font variant="button">Search</Font>
        </Button>
      </form>

      <br />

      <Font variant="h1" id="place"></Font>
      <Font variant="caption">Local Time: {/*props.time*/}</Font>
      {/*<div id="mw">
        {props.weather ? weather() : ""}

      </div>
  */}
      <div id="map" style={{ height: "600px" }}></div>
      <Grid container spacing={5} justify="space-around">
        <ArticleList news={news}/>
        {/*props.news.map(i => (
          <Grid item xs={12}>
            <Card id="article" elevation={5} align="center" key={i.title}>
              <Paper>
                <Font variant="h3">{i.title}</Font>
                <Font variant="body1" align="left">
                  {i.source} | {i.published}
                </Font>
                <img src={i.img} id="aImg" alt={i.title}></img>
                <Font variant="body1" align="left">
                  {i.snippet}
                </Font>
                <Button variant="contained" href={i.url} target="blank">
                  <Font variant="button">Learn More</Font>
                </Button>
              </Paper>
            </Card>
          </Grid>
        ))}
        */}
      </Grid>
    </Container>
  );
};

export default Location;
