import {Loader,LoaderOptions} from "google-maps";
import {url,key} from "./config";
const axios = require("axios");    
export const GetLocation = async ({searchString,map}) => {
  const area = searchString
  //* Google Maps
  let res = await axios(url.google, {
    params: {
      address: area,
      key: key.google
    }
  });
  let data = res.data;
  const name = data.results[0].address_components[0].long_name;
  const arealat = data.results[0].geometry.location.lat;
  const arealing = data.results[0].geometry.location.lng;

  let zoom;
  data.results[0].types[0] === "locality" ? (zoom = 10) : (zoom = 5);


    map.setCenter(new google.maps.LatLng(arealat, arealing));
    map.setZoom(zoom);

  getTimes(arealat,arealing)
  getNews(name)
  getWeather(name)
};

const getTimes = async (arealat, arealing) => {
  //* Time Api
  const res = await axios.get(url.time, {
    params: {
      format: "json",
      by: "position",
      lat: arealat,
      lng: arealing,
      key: key.time
    }
  });

  const data = res.data;
  const thetime = data.formatted.split(" ");
  const ftime = thetime[1];
  //Set Time
  //this.setState({ time: ftime });
};

const getNews = async name => {
  //* News Api
  try {
    const res = await axios.get(url.news, {
      params: {
        q: name,
        apiKey: key.news,
        pageSize: 6
      }
    });
    let articles = res.data.articles;
    if (articles.length < 0) {
      alert("No News Found");
    } else {
      articles.map(article => {
        const date = article.publishedAt.split("T");
        return this.setState(prevState => ({
          news: [
            ...prevState.news,
            {
              title: article.title,
              url: article.url,
              snippet: article.description,
              img: article.urlToImage,
              source: article.source.name,
              published: date[0]
            }
          ]
        }));
      });
    }
  } catch (er) {
    console.log(er);
  }
};

const getWeather = async location => {
  try {
    const res = await axios(url.weather, {
      params: {
        q: location,
        units: "imperial",
        appid: key.weather
      }
    });
    const data = res.data;
    const name = data.name;

    const weather = data.weather[0].main;
    const temp = Math.ceil(data.main.temp);
    const wind = data.wind.speed;
    //Set The State
    //this.setState(() => ({ weather: { name, weather, temp, wind } }));
  } catch (er) {
    console.log(er);
  }
};
